// Fork of "CoordinateGrid_DrawMathFunction2" by miyu. https://shadertoy.com/view/MfVcDK
// 2024-11-27 15:44:54

#define grid_subdivision 15.
#define AA 4

float p(in float a, in int b){
    float r = a;
    for(int i = 2; i<10; i++){ r = r*a; if(i>=b){ break;} }
    return r;
}

vec2 UV(vec2 uv)
{
    return grid_subdivision*(uv-.5*iResolution.xy)/min(iResolution.x,iResolution.y);
}

vec3 CoordinateGrid(vec2 uv)
{
    vec3 col=vec3(0.4);   
    vec2 grid = floor(mod(uv,2.));
    if(grid.x==grid.y) col=vec3(.6);

    col = mix(col,vec3(1.,0.,0.),smoothstep(1.1*fwidth(uv.x),fwidth(uv.x),abs(uv.x)));
    col = mix(col,vec3(0.,1.,0.),smoothstep(1.1*fwidth(uv.y),fwidth(uv.y),abs(uv.y)));

    return col;
}


float func(float x)
{
    return sin(x);
}

float func(float x,float y)
{
    return ( 2.0 * p(x,3) * p(y,3) )-( p(x,4) * p(y,2) )-( p(x,2) * p(y,4) ) + 36.0;
}

float plot_func(vec2 uv)
{
    float y = func(uv.x,uv.y);
   //float y = func(uv.x);
    return smoothstep(y-0.01,y+0.01,uv.y);
}

float draw_func(vec2 uv)
{
    float f=0.;
    for(int i=0;i<AA;i++)
    {
        for(int j=0;j<AA;j++)
        {
            vec2 offset = (vec2(float(i),float(j))-.5*float(AA))/float(AA)*2.;
            f+=plot_func(UV(uv+offset));
        }
    }
    if(f>float(AA*AA)/2.)
        f=float(AA*AA)-f;
    f=f*2./float(AA*AA);
    return f;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=UV(fragCoord);

    vec3 col = vec3(0.);

    col =CoordinateGrid(uv);

    float se = draw_func(fragCoord);
    col=mix(col,vec3(1.),se);

    fragColor = vec4(col,1.0);
}
