vec3 rgb(float r, float g, float b) {
    return vec3(r / 255.0, g / 255.0, b / 255.0);
}

float variation(vec2 v1, vec2 v2, float strength, float speed) {
	return sin(
        dot(normalize(v1), normalize(v2)) * strength + iTime * speed
    ) / 100.0;
}

vec4 circle(vec2 uv, float rad, vec3 color) {
    float d = length(uv) - rad;
    d += variation(uv, vec2(0.0, 1.0), 5.0, 4.0);
    d -= variation(uv, vec2(1.0, 0.0), 5.0, 4.0);
    float t = smoothstep(0.0, 0.005, d);
    return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    float radius = 0.4;

    // Background layer
    vec4 backgroundLayer = vec4(rgb(210.0, 222.0, 228.0), 1.0);

    // Circle
    vec3 red = rgb(225.0, 95.0, 60.0);
    vec4 circleLayer = circle(uv, radius, red);

    // Blend the two
    fragColor = mix(backgroundLayer, circleLayer, circleLayer.a);

}
