vec2 distort(vec2 uv, float time) {
    float repeat = 2.0;
    float speed = 4.0;
    float power = 0.02;
    return uv + vec2(
        sin(repeat * 3.14 * uv.x + time * speed) * power,
        sin(repeat * 3.14 * uv.y + time * speed) * power);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = distort(uv, iTime);
	fragColor = vec4(texture(iChannel0, uv) );
}
