void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float angle = iTime * .13 ;
    for (int i = 0; i < 13; i++) {
        uv = (abs(uv) - .13) * mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    }
    float timer = (sin(iTime) + cos(iTime)) / 2.;
    fragColor = vec4(fract(uv.y * 13. + timer), fract(uv.y * 13. - timer), sin(uv.x * 13. + timer) + cos(uv.x * 13. - timer), 2.);
}
