// Input video su iChannel0
// Texture di sfondo stellato su iChannel1

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Calcola le coordinate UV
    vec2 uv = fragCoord / iResolution.xy;

    // Colore del pixel del video
    vec4 videoColor = texture(iChannel0, uv);

    // Colore del pixel della texture di sfondo
    vec4 backgroundColor = texture(iChannel1, uv);

    // Colore verde da rimuovere
    vec3 greenKey = vec3(0.0, 1.0, 0.0);

    // Parametri di controllo per il chroma key
    float threshold = 0.6333;    // Sensibilit√† al verde, aumenta per rimuovere pi√π verde
    float fadeWidth = 0.2;    // Sfuma i bordi per eliminare l'alone, aumenta per un bordo pi√π morbido

    // Calcola la distanza dal colore verde
    float diff = length(videoColor.rgb - greenKey);

    // Calcola il valore alpha usando il threshold e il fadeWidth
    float alpha = smoothstep(threshold, threshold + fadeWidth, diff);

    // Combinazione del video con il background stellato
    vec3 finalColor = mix(backgroundColor.rgb, videoColor.rgb, alpha);

    // Output del colore finale
    fragColor = vec4(finalColor, 1.0);
}
