float random(float n) {
    return fract(sin(n)*1e4);
}
float noise(float x) {
    float i = floor(x);
    float f = fract(x);

    return mix(random(i),random(i+1.),smoothstep(0.,1.,f));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.);
    vec2 st = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec2 pos = st*10.;
	float a = atan(pos.x,pos.y);
    float r = length(pos);

    float b = noise(cos(refract(cos(a+iTime),sin(a+iTime),1.))-cos(a-r)+iTime);

    color = vec3(b);
    color *= vec3(b=cos(r+a+(iTime))*1.5,b,b*4.);

    fragColor = vec4(color,1.0);
}
