void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Define the matrices
    mat4 modelMatrix = mat4( 1.0 );
    mat4 viewMatrix = mat4( 1.0 );
    mat4 projectionMatrix = mat4( 1.0 );

    // Set the values of the matrices
    modelMatrix[0][0] = 0.5 * cos( iTime * 0.5 );
    modelMatrix[1][1] = 0.5 * sin( iTime * 0.5 );
    modelMatrix[3][2] = sin( iTime );

    viewMatrix[3][2] = -5.0;

    projectionMatrix[1][1] = 1.0 / iResolution.y;
    projectionMatrix[0][0] = 1.0 / iResolution.x;

    // Define the vertex position
    vec4 vertexPosition = vec4( fragCoord.xy, 0.0, 1.0 );

    // Transform the vertex position using the matrices
    vec4 transformedVertexPosition = projectionMatrix * viewMatrix * modelMatrix * vertexPosition;

    // Calculate the final fragment color
    fragColor = vec4( transformedVertexPosition.xyz / transformedVertexPosition.w, 1.0 );
}
