float Circle(vec2 uv, vec2 p, float r, float blur){

       float d = length(uv-p);
       float c = smoothstep(r, r-blur, d);

       return c;

}

float Band(float t, float start, float end, float blur){
       float step1 = smoothstep(start-blur, start+blur, t);
       float step2 = smoothstep(end+blur, end-blur, t);

       return step1*step2;

}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
       float band1 = Band(uv.x, left, right, blur);
       float band2 = Band(uv.y, bottom, top, blur);

       return band1*band2;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{

       vec2 uv = fragCoord.xy / iResolution.xy;

       uv -= .5;
       uv.x *= iResolution.x/iResolution.y;

       vec3 col = vec3(0.);


       float pumpkin = Circle(uv, vec2(0.), .45, .01);        //pumpkin base

       pumpkin -= Circle(uv, vec2(0., -.05), .2, .01);              //body
       pumpkin -= Circle(uv, vec2(0., -.3), .1, .01);        //head

       float x1 = uv.x;
       float y1 = uv.y;

       y1 -=x1*0.8;

       float x2 = uv.x;
       float y2 = uv.y;

       y2 -=x2*-0.8;

       float x3 = uv.x;
       float y3 = uv.y;

       y3 -=x3*0.1;

       pumpkin -= Rect(vec2(x1,y1), .05, .3, 0., 0.03, 0.001);    //legs right
       pumpkin -= Rect(vec2(x1,y1), .09, .35, -0.13, -0.10, 0.001);   
       pumpkin -= Rect(vec2(x2,y2), 0.1, .36, 0.09, 0.12, 0.001);   
       pumpkin -= Rect(vec2(x2,y2), 0.1, .31, -0.03, 0., 0.001);

       pumpkin -= Rect(vec2(x2,y2), -.3, 0., 0., 0.03, 0.001);    //legs left
       pumpkin -= Rect(vec2(x2,y2), -.35, 0., -0.13, -0.10, 0.001);   
       pumpkin -= Rect(vec2(x1,y1), -0.35, 0., 0.09, 0.12, 0.001);   
       pumpkin -= Rect(vec2(x1,y1), -0.3, 0., -0.03, 0., 0.001);

       pumpkin -= Rect(vec2(x3,y3), -.007, 0.007, 0., 0.4, 0.001);   //string

       pumpkin += Rect(vec2(x3,y3), -.04, 0.03, 0.444, 0.6, 0.001);   //stem

       col = vec3(1.,0.5,0.)*pumpkin;

       fragColor = vec4(col, 1.0);

       fragColor = vec4(col, 1.0);

 }
