void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates
    vec2 uv = fragCoord/iResolution.xy;

    // Center of the magnification (fixed for Rolex-like effect)
    vec2 center = vec2(0.5, 0.5);

    // Magnification parameters
    float radius = 0.1;  // Size of the cyclops lens
    float magnification = 2.5;  // Typical Rolex cyclops magnification

    vec2 d = uv - center;
    float r = length(d);

    if (r < radius) {
        // Rolex cyclops-like magnification
        vec2 distortedUV = center + d / magnification;

        // Sample the texture
        vec4 color = texture(iChannel0, distortedUV);

        // Subtle glass effect
        vec3 glassColor = vec3(0.98, 0.98, 1.0);  // Very slight blue tint
        color.rgb = mix(color.rgb, color.rgb * glassColor, 0.05);

        // Add a subtle highlight
        float highlight = smoothstep(radius, radius * 0.9, r) * 0.1;
        color.rgb += highlight;

        fragColor = color;
    } else {
        // Add a subtle edge highlight
        float edge = smoothstep(radius, radius + 0.002, r) - smoothstep(radius + 0.002, radius + 0.004, r);
        vec4 color = texture(iChannel0, uv);
        fragColor = mix(color, vec4(1.0), edge * 0.5);
    }
}
