void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = fragCoord.xy/iResolution.xy;
    float edgeBlur = 0.007;
    float waveDiff = 20.0;
    float waveWidth = 5.0;
    float waveHeight = 10.0;
    vec3 bgCol = vec3(154.0, 82.0, 227.0);
    vec3 colDiff = vec3(0.1, 0.05, 0.1);

    vec3 col = vec3(bgCol.r / 255.0 - 0.1 * xy.y, bgCol.g / 255.0 - 0.15 * xy.y, bgCol.b / 255.0 - 0.05 * xy.y);

    if (xy.y <= cos((xy.x + float(cos(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) {
        for (int i = 0; i < 3; i++) {
            col[i] += colDiff[i];
        }
    }
    else if (abs((cos((xy.x + float(cos(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) - xy.y) < edgeBlur) {
        for (int i = 0; i < 3; i++) {
            col[i] += colDiff[i] * (edgeBlur + ((cos((xy.x + float(cos(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) - xy.y)) * (1.0 / edgeBlur);
        }
    }

    if (xy.y <= sin((xy.x + float(sin(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) {
        for (int i = 0; i < 3; i++) {
            col[i] += colDiff[i];
        }
    }
    else if (abs((sin((xy.x + float(sin(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) - xy.y) < edgeBlur) {
        for (int i = 0; i < 3; i++) {
            col[i] += colDiff[i] * (edgeBlur + ((sin((xy.x + float(sin(iTime)) / waveDiff + iTime * 0.05) * waveWidth) / waveHeight + 0.25) - xy.y)) * (1.0 / edgeBlur);
        }
    }

    fragColor = vec4(col.r, col.g, col.b, 1.0);
}
