vec2 f_o_rotd(vec2 o_trn, float n_ang_nor){
    float n_tau = radians(360.);
    float n_sin = sin(n_ang_nor*n_tau);
    float n_cos = cos(n_ang_nor*n_tau);
    mat2 o_matr = mat2(
        n_cos, -n_sin, 
        n_sin, n_cos
    );
    return o_trn*o_matr;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    o_trn*=.5;
    float n_dcntr = length(o_trn);
    o_trn = f_o_rotd(o_trn, iTime*.01*sin(n_dcntr*22.+iTime)*.002);
    vec2 o2 = (fragCoord.xy / iResolution.xy)*vec2(2.,1.);
    float n_tau = radians(360.);
    float n = sin(n_dcntr*n_tau*3.+iTime)*.5+.5;

    float n2 = 1.;
    float n_its = 4.;
    vec3 o_col = vec3(
       1.
    );
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        float n_it_nor = n_it / n_its;
        vec2 o = o_trn - vec2(
            sin(n_it_nor*n_tau),
            cos(n_it_nor*n_tau)
        )*.2;
        float ntmp = length(o);
        ntmp = sin(ntmp*n_tau*(1./n_dcntr*3.)+iTime)*.5+.5;
        ntmp = pow(ntmp, 1./3.);
        float nd2 = sin(pow(n_dcntr, 1./20.)*3.)*.5+.5;
        o_col *= vec3(
            sin(ntmp*n_tau*((.2/n_dcntr)+.01)+iTime+(nd2*2.))*.5+.5,
            sin(ntmp*n_tau*((.2/n_dcntr)+.01)+iTime+(nd2*3.))*.5+.5,
            sin(ntmp*n_tau*((.2/n_dcntr)+.01)+iTime+(nd2*4.))*.5+.5
        )+0.1;
    }


    fragColor = vec4(o_col,1.);
}
