void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 color1 = vec3(1, 1, .5);
	vec3 color2 = vec3(0.10, 0.30, 1.);
	vec3 pixel;
	float aze = (sin(2.*iTime)*iResolution.x/4.+iResolution.x/2.);

	// sugar syntax for "if" conditional. It says
	// "if the x coordinate of a pixel is greater than the half of
	// the width of the screen, then use color1, otherwise use
	// color2."
	pixel = ( fragCoord.x > aze ) ? color2 : color1;

    fragColor = vec4(pixel, 1.0);}
