#define PI 3.14159265359

// Plot a line on Y using a value between 0.0-1.0
float plot(vec2 st, float pct) {
   return smoothstep(pct-0.3, pct, st.y) - 
         smoothstep(pct, pct+0.3, st.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
   vec2 st = fragCoord.xy/iResolution.xy;

   float scalar = sin(st.x * PI);
   float whereAreWe = st.x;
   float plotMe = plot(st, scalar);

   if (0.0 == plotMe) {
       fragColor = vec4(0.0, 0.0, 0.0, 1.0);
       return;
   }

   vec4 color;

   if (0.0 <= whereAreWe && whereAreWe < 0.1333334) {
       // Red Band
       float r = 255.0/255.0;
       float g = 38.0/255.0;
       float b = 0.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.1333334 <= whereAreWe && whereAreWe < 0.1666667) {
       float s = smoothstep(0.1333334, 0.1666667, whereAreWe);

       float g = mix(38.0/255.0, 155.0/255.0, s);

       color = vec4(1.0, g, 0.0, 1.0) * plotMe;
   } else if (0.1666667 <= whereAreWe && whereAreWe < 0.3000000) {
       // Orangish band
       float r = 255.0/255.0;
       float g = 155.0/255.0;
       float b = 0.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.3000000 <= whereAreWe && whereAreWe < 0.3333333) {
       float s = smoothstep(0.3000000, 0.3333333, whereAreWe);

       float r = mix(255.0/255.0, 254.0/255.0, s) * plotMe;
       float g = mix(155.0/255.0, 255.0/255.0, s) * plotMe;

       color = vec4(r, g, 0.0, 1.0);
   } else if (0.3333333 <= whereAreWe && whereAreWe < 0.4666667) {
       // Yellow band
       float r = 254.0/255.0;
       float g = 255.0/255.0;
       float b = 0.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.4666667 <= whereAreWe && whereAreWe < 0.5000000) {
       float s = smoothstep(0.4666667, 0.5000000, whereAreWe);

       float r = mix(254.0/255.0, 0.0/255.0, s);
       float g = mix(255.0/255.0, 155.0/255.0, s);

       color = vec4(r, g, 0.0, 1.0) * plotMe;
   } else if (0.5000000 <= whereAreWe && whereAreWe < 0.6333337) {
       // Green band
       float r = 0.0/255.0;
       float g = 155.0/255.0;
       float b = 0.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.6333337 <= whereAreWe && whereAreWe < 0.6666667) {
       float s = smoothstep(0.6333337, 0.6666667, whereAreWe);

       float r = mix(0.0/255.0, 25.0/255.0, s);
       float g = mix(155.0/255.0, 0.0/255.0, s);
       float b = mix(0.0/255.0, 255.0/255.0, s);

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.6666667 <= whereAreWe && whereAreWe < 0.8000000) {
       // Blue band
       float r = 25.0/255.0;
       float g = 0.0/255.0;
       float b = 255.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.8000000 <= whereAreWe && whereAreWe < 0.8333333) {
       float s = smoothstep(0.8000000, 0.8333333, whereAreWe);

       float r = mix(25.0/255.0, 206.0/255.0, s);
       float g = mix(0.0/255.0, 0.0/255.0, s);
       float b = mix(255.0/255.0, 154.0/255.0, s);

       color = vec4(r, g, b, 1.0) * plotMe;
   } else if (0.8333333 <= whereAreWe && whereAreWe < 1.0000000) {
       // Purplish band
       float r = 206.0/255.0;
       float g = 0.0/255.0;
       float b = 154.0/255.0;

       color = vec4(r, g, b, 1.0) * plotMe;
   }

   fragColor = color;
}
