void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//multiply both coord by 1/Resolution.x so that things stay in proper proportion
	vec2 uv = fragCoord.xy / iResolution.xx;

	//maybe eventually I replace this with a proper parametric function
	vec2 uv_scroll = vec2(iTime/32.0,0);

	//brightness of the red and blue channels to add to the sampling texture coordinate
	vec2 offset = 	texture(iChannel1,uv + uv_scroll,.5).yz * .085;

	//warp the background texture
	vec3 color = 	texture(iChannel0,uv+offset,.5).xyz;

	//sample "warp map" ordinarily so the pattern can be followed
	vec3 color2 = 	texture(iChannel1,uv + uv_scroll,.5).xyz;

	/*	for whatever reason, Texture2D without the offset returns half-precicion floats
		for another strange reason, I cannot find a way to convert 16bit floats to 32bit floats	*/

	//wanted to do something interesting, but the cross product just looked ugly, so mix instead
	//fragColor=vec4(mix(color,color2,.33),.0);
	  fragColor=vec4(color+color2*(1.0/3.0),.0); //brighten things up; good old FMAD
}
