/*
	This is my first shader :)

	I hope you like it!

	(could be laggy, sorry)
*/


//this is how many snowflakes there are on the screen
#define SnowCount 200


float time = 0.0;
vec2 uv;

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand(float x){ return rand(vec2(x,1.0));}

float snow(vec3 pos, vec2 uv, float o){
	vec2 d = (pos.xy - uv);
	float a = atan(d.y,d.x)+sin(time*1.+o)*10.;

	float dist = d.x*d.x + d.y*d.y;

	if(dist < pos.z/400.){
		float col = 0.0;
		if(sin(a*8.) < 0.0){col=1.0;}
		if(dist<pos.z/800.0){col+=1.0;}
		return col*pos.z;
	}

	return 0.0;
}

float col(vec2 c){
	float color = 0.0;

	for(int i=0;i< SnowCount;++i){
		float o = rand(float(i)/3.)*float(SnowCount)*2.0;

		float z = rand(float(i)+13.0);

		float x = 1.8 - (1.8*2.0) * (rand(floor((time*((z+1.0)/2.0)+o)/2.)) + sin(time*o/1000.0)/10.0);

		float y = 1.0-mod((time*((z+1.0)/2.0))+o, 2.0);

		color += snow(vec3(x,y,z),c,o);
	}

	if(c.y < sin(c.x*4.)/60.-0.8){
		color = -c.y+sin(c.x*4.)/60.;
	}

	return color;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  	 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 - 1.0;
	vec2 p = uv;
	p.x *= iResolution.x / iResolution.y;

	time = iTime/4.0;

	fragColor = vec4(col(p));
}
