float iso(vec3 p)
{
	float g  = 0.0;
	float kk = 0.01;
	float lp = 0.0;
	lp += texture(iChannel0, p.xz * kk * vec2(0.5, 2.0).xy).r;
	lp -= texture(iChannel0, p.xz * kk * vec2(0.7, 1.5).yx).r;
	g = (4.0 + lp) - dot(abs(p), vec3(0, 1, 0));
	return g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float ti = iTime;
	vec2  uv  = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
	vec3  pos = vec3(-ti * 2.0, 0, ti * 5.0);
	vec3  dir = -normalize(vec3(uv * vec2(iResolution.x / iResolution.y, -1.0), 1.0)).zxy;

	float t = 0.0;
	for(int i = 0 ; i < 50; i++)
		t += iso(t * dir + pos) * 0.5;
	vec3  ip  = t * dir + pos;
	float aa  = t * 0.025 + abs(iso(ip + 0.25)) + texture(iChannel1, ip.xz * 0.07).r * 0.2;
	vec3  col = vec3(aa) * vec3(1,2,3) * 0.25;
	col       = sqrt(col);
	fragColor = vec4(col, t);
}
