const float PI = 3.14159265359;
const float INV_PI = 0.5 * 0.31830988618;
const float kEpsilon = 0.00001;

struct LightingData 
{
	vec3 L;
	vec3 N;
	vec3 H;
    vec3 I;
    vec3 GI;	
};

float R(in vec2 uv) 
{
  return fract(sin(dot(normalize(uv.xy) ,vec2(12.9898,78.233))) * 43758.5453);
}

//Ray are thrown from the origin
//d is normalized
bool RaySphereIntersect(in vec3 rd, in vec3 sphereCenter, in float radius, out vec3 position)
{		

	float s = dot(sphereCenter, rd);
	float r2 = radius*radius;
	float l2 = dot(sphereCenter,sphereCenter);
	if (s<0. && l2>r2) 
	{
		return false;
	}
	float m2 = l2 - s*s;
	if (m2>r2)
	{
		return false;
	}

	float q = sqrt(r2-m2);
	float t=0.;
	if (l2>r2)
	{
		t=s-q;
	}
	else
	{
		t=s+q;
	}
	position=t*rd;
	return true;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float head=iTime*0.5; float pitch= 0. ,roll=0.;
	float ch=cos(head); float cp=cos(pitch); float cr=cos(roll); 
	float sh=sin(head); float sp=sin(pitch); float sr=sin(roll); 
	mat3 rot;
	rot[0] = vec3(cr*ch-sr*sp*sh,sr*ch+cr*sp*sh,-cp*sh);
	rot[1] = vec3(-sr*cp,cr*cp,sp);
	rot[2] = vec3(cr*sh+sr*sp*ch,sr*sh-cr*sp*ch,cp*ch);

	// camera
	vec2 q = fragCoord.xy / iResolution.xy;
	float cond = R(q);

    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;    
	vec3 ro = rot*vec3(1.0,0.0,0.);
	vec3 ww = normalize(vec3(0.0,0.0,0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.4*ww); //ray direction in world space

	vec3 sphereCenter = 10.*ww; 
	float radius = 4.;

	vec3 finalColor = vec3(0.0,0.0,0.0); 
	vec3 lightPosition, position;

	lightPosition = sphereCenter + 6.*radius*(-0.5*ww + .25*uu+.4*vv);				

	if ((cond>=sin(iTime)) && RaySphereIntersect(rd, sphereCenter,  radius, position))
	{
		//Rendering equation		
		LightingData data;
		data.L = normalize(vec3(lightPosition-position));
		data.N = normalize(position-sphereCenter);
		data.I = vec3(2000.)/pow(length(lightPosition-position),2.);
		data.GI = (texture(iChannel1,data.N)).xyz;

		vec3 V = normalize(-position);
		data.H = normalize(data.L+V);		
		vec3 reflected = reflect(rd,data.N); vec3 albedo;

		vec3 p = normalize(position-sphereCenter);
		vec2 coords = (vec2(acos(p.y),atan(p.x/p.z)));
     	coords = INV_PI*vec2(coords.x,PI*0.5+coords.y);		//coords is now between 0 and 1
       	coords = floor(20.*coords);		
		float t = 0.;
		if ((coords.x+coords.y != floor((coords.x+coords.y)/2.)*2.))
			t = 1.;
		const vec3 red = vec3(1.,0.,0.0);
		const vec3 white = vec3(1.,1.,1.0);		
		albedo = mix(white,red,t);

		vec3 specularColor = (texture(iChannel0,reflected)).xyz;
		vec3 diffuseContrib = data.I*albedo*INV_PI*max(0.0,dot(data.N,data.L));
		vec3 specularContrib = data.I*specularColor*pow(max(0.0,dot(data.N,data.H)),100.);		

		float N2 = dot(data.N,data.N);
		vec3 GIdiffuseContrib = data.GI*albedo*INV_PI*N2;
		vec3 GIspecularContrib = data.GI*specularColor*pow(N2,100.);		

		finalColor = diffuseContrib + specularContrib + 0.25*(GIdiffuseContrib + GIspecularContrib);			



	}
	else
	{
        finalColor=texture( iChannel0, rd ).xyz;
	}


	fragColor = vec4(finalColor,1.0);
}
