float circle(vec2 p,float radius){
p.x=mod(p.x,radius*2.0)-radius;
float val=length(p)-radius;
    if(val>0.0)return 0.0;
    return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv=uv*2.0-1.0;
	vec4 color=vec4(1,1,1,1);

    float val=0.0;

    if(uv.y<0.0){
    uv.x+=iTime*0.1;
    val+=circle(uv,0.5); 
    val+=circle(uv+vec2(0.5+sin(iTime)*0.175,0.0),0.5);  
        val=1.0-val;
    }

    fragColor = color*val;
}
