//#define SS
#define GRID

const float sqrt2 = 1.414213;
const float rads = 2. * 3.14159;
const float sinB = 3.14159 * 2.;
const float waveScale = 8.0;

float lineCount;
float timeA;
float timeB;
vec2 s;
vec3 target;

float circ(float d) {
    return sin(d * sinB * lineCount - timeA) + 1.;
}

float circMod(float d) {
    return mod(d * lineCount - timeA, 1.);
}

const vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);

vec3 hsv2rgb(vec3 c)
{
	// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}



vec4 point(vec3 pos) {
    float d = dot(target, pos);

    float circles = sin((d + s.y) * sinB - timeB) * 0.5 + 0.5;

    float w = pos.y + sin((d * d + s.x) * sinB * waveScale + timeB) / lineCount;
    float waves = circ(w);

    #ifdef GRID
    float lines = circ(d);
    vec3 hsv = vec3(circles,waves + lines,waves - lines);
    #else
    vec3 hsv = vec3(circles,waves,waves);
    #endif
    return vec4(hsv2rgb(hsv),1.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = vec2(1., 1.) + iMouse.xy / iResolution.xy;
    s = vec2(1., 1.) + texture(iChannel1, fragCoord.xy).xy * 0.2;

    lineCount = m.y * m.y * 15.;
    timeA =  m.x * m.x * 40. * iTime;
    timeB = timeA * 0.005;
    float t = mod(timeB, rads);
    target = vec3(cos(t), sin(t), 0);


    // ss based on https://www.shadertoy.com/view/XslSW7
	vec3 p = vec3(fragCoord.xy / iResolution.x, 0.);    
	#ifdef SS
    float delta = 1.0 / iResolution.x;
    vec4 color = (point(p+delta*vec3(0.25,0.00,1.)) +
			      point(p+delta*vec3(0.75,0.25,1.)) +
                  point(p+delta*vec3(0.00,0.50,1.)) +
                  point(p+delta*vec3(0.50,0.75,1.))) / 4.0;
    #else
    vec4 color = point(p);
    #endif
    fragColor = color;
}
