void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int size = 4;

    vec2 parts[9];

    float radi = iResolution.x*0.1;

    float freq = 0.5;

    for(int i = 0;i<9;i++){
        float modi = float(i);
        radi+=modi+radi*0.1*sin(iTime*modi+modi);
		parts[i] = vec2(iResolution.xy*0.5) + vec2(radi*cos(iTime*(freq+0.5*modi)),radi*sin(iTime*(freq+0.5*modi)));
    }



    vec2 uv = fragCoord.xy;
    for(int i = 0;i<9;i++){

        float amp = 0.0;

        float disX = uv.x - parts[i].x;
    	float disY = uv.y - parts[i].y;

   		if(sqrt(disX*disX+disY*disY) < iResolution.x*0.00005){
        	amp += 2.0;
    	}
    	else{
        	disX = disX/iResolution.x;
        	disY = disY/iResolution.x;
			amp += 0.0025/(disX*disX+disY*disY);    
   		}

        if(i<3){
            fragColor += vec4(amp*0.2,amp*0.8,amp*0.2,amp);
        }
        else if(i<6){
            fragColor += vec4(amp*0.2,amp*0.2,amp*0.8,amp);
        }
        else{
            fragColor += vec4(amp*0.8,amp*0.2,amp*0.2,amp);
        }


    }

}
