float COLOR_DEPTH =2.0;


// Simultaneous presentation
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mousePos = iMouse.x/iResolution.x;
	vec2 uv = fragCoord.xy / iResolution.xy;
    float value = (texture(iChannel1,uv).r*0.2125+texture(iChannel1,uv).g*0.715+texture(iChannel1,uv).b*0.0721);
    if(uv.x<mousePos-0.002){
		fragColor = vec4(floor(value*COLOR_DEPTH)/(COLOR_DEPTH-1.0),
                         floor(value*COLOR_DEPTH)/(COLOR_DEPTH-1.0),
                         floor(value*COLOR_DEPTH)/(COLOR_DEPTH-1.0),
                         1.0);
    }else if(uv.x>mousePos+0.002){ 
		fragColor = vec4(floor(value*(COLOR_DEPTH-.5)+rand2(fragCoord+iTime)*1.0)/(COLOR_DEPTH-1.0),
                         floor(value*(COLOR_DEPTH-.5)+rand2(fragCoord+iTime)*1.0)/(COLOR_DEPTH-1.0),
                         floor(value*(COLOR_DEPTH-.5)+rand2(fragCoord+iTime)*1.0)/(COLOR_DEPTH-1.0),
                         1.0);
    }else{
     	fragColor = vec4(1.0,0.0,0.0,1.0);   
    }


}
