float s(float t) { return sin(t); }
float c(float t) { return cos(t); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = mix(uv, uv + vec2(c(t*1.01), s(t*1.02)) * 0.1 * texture(iChannel0, uv).x, texture(iChannel0, uv).y + 0.7);

    fragColor = vec4(s(t), c(s(t*0.2)*4.0)+0.3, s(t*t), 1.0);
    fragColor.x = s(uv.x * 26.0) * c(uv.y * 17.0);

    vec4 m = texture(iChannel3, round(uv*32.0)/32.0);

    float dither = s(fragCoord.x * 1.0) * c(fragCoord.y * 0.5);
    //vec2 cc = vec2(c(t), s(t));
    float center = pow(1.0 - length(uv - vec2(0.5, iResolution.y / iResolution.x)) * abs(s(t*0.3+m.x)), 2.0);
    //float center = length(cc);

    //fragColor *= dither * 0.7 + abs(sin(t*8.0)) * dither * 0.3 * center * texture(iChannel3, uv) * fragColor.x;
    fragColor *= dither * 0.7 + abs(sin(t*8.0)) * dither * 1.4 * center * m.x * fragColor.x;

    //fragColor.x = texture(iChannel3, uv).x;  
	//fragColor.x = 1.0-m.x;
    fragColor.y = mix(fragColor.y, center * texture(iChannel0, uv).x, 0.3*c(t*0.001));
    fragColor.z = fragColor.x * fragColor.y;
    fragColor.z *= 2.0;
}
