void mainImage( out vec4 z, vec2 b )
{
    z -= z;
    b /= 2e3; // b /= iResolution.y;

    vec2 A,B,C,D,E,G;

    A=B=C-=C;
    B.x = 1.7320; // sqrt(3)
    C.y = 1.;


    for(int i = 0 ; i < 15 ; i++)
    {
        D = (2. * A + B)/3.;

        G = b - D;
        E = B - D;

        // z.a = cos( angle )
        z.a = dot(G,E)/(length(G)*length(E));

        E = (B + C)/2.;

        // COS(2PI/3) = -1/2
        // COS( PI/3) =  1/2

        // ACD Triangle
        if( z.a < -.5 ) {
            B = C; 
            z.r = .9 - z.g;
        	// A stays the same
        // EBD Triangle
        } else if( z.a > .5 ) {
            A = E;
            z.g = 1. - z.b;
        	// B stays the same
        // ECD Triangle
        } else {
            A = E;
            B = C;
        }

        z.rgb = 1.1 - z.gbr;
        C = D;

    }


}
