void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //world to texture coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

    //from openGL texture() samples texels from the texture bound to sampler at texture coordinate P
    //notice that our 'sampler' here is an actually an audio sampler (thanks shadertoy!)
	float wave = texture(iChannel0, vec2(uv.x, 0.5)).x;

	//the color we want the wabe to be drawn in
	vec3 col = vec3(0.0);

    //drawing waveform (or like undrawing not waveform, I guess)
	col += 1.0 -  smoothstep(0.0, 0.1, abs(wave - uv.y));

	//output 
	fragColor = vec4(col,1.0);
}
