const float inf = 1./.0;

mat3 rx(float a){float s=sin(a),c=cos(a);return mat3(1.,0.,0.,0.,c,s,0.,-s,c);}
mat3 ry(float a){float s=sin(a),c=cos(a);return mat3(c,0.,s,0.,1.,0.,-s,0.,c);}
mat3 rz(float a){float s=sin(a),c=cos(a);return mat3(c,s,0.,-s,c,0.,0.,0.,1.);}

float fq(float t) {return texture(iChannel0,vec2(t,0.25)).r;}
vec3 hsv(vec3 c){
    vec4 K=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+K.xyz)*6.-K.www);
    return c.z*mix(K.xxx,clamp(p-K.xxx,0.,1.),c.y);
}

float map(vec3 p)
{
    p*=rz(p.z*0.1+iTime*0.2);
    return length(mod(p,2.)-1.)-.2-fq(0.05)*0.2;
}

float df(vec3 o, vec3 d, out vec3 p)
{
    int i;
    float rd=.0;
    for (i=0;i<30;++i)
    {
        p=o+d*rd;
        float td=map(p);
        rd+=td;
        if (td<.01) break;
    }
    return 1.-float(i)/30.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy -.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 o=vec3(uv,-iTime),d=vec3(uv,-1.),mp;
	float md=df(o,d,mp);
    vec3 c=hsv(vec3(mp.x*0.02 + mp.y*.1+iTime*0.1,1.,1.)); 
	fragColor = vec4(md*c,1.0);
}
