float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 light;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    light = vec2(iMouse.x/iResolution.x, iMouse.y/iResolution.y);
    vec2 pos = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
    float dist = sqrt((pos.x-light.x)*(pos.x-light.x) + (pos.y-light.y)*(pos.y-light.y));
	float col = 0.5 - sqrt(dist) + 0.075 * rand(pos) * rand(pos * vec2(iTime, iTime));
    if (col < 0.01)
        col = 0.;
	fragColor = vec4(col, col, col, 1.0);
}
