vec2 Rain( vec2 uv, float t) {

    t *= 40.;

    //augment square to rect
   	vec2 a = vec2(3., 1.);
    vec2 st = uv*a;

    //get id of each column
    vec2 id = floor(st);

    st.y += t*.22;

    //psuedo-random offset grid y
    float n = fract(sin(id.x*76.12)*768.32);
    st.y += n;
    uv.y += n;
    id = floor(st);
    st = fract(st)-.5;

    t += fract(sin(id.x*76.12+id.y*1234.5)*768.32)*6.28;
    float y = -sin(t + sin(t+sin(t)*.5))*.4;
    vec2 p1 = vec2(0., y);
    vec2 o1 = (st-p1)/a;
    float d = length(o1);

   	float m1 = smoothstep(.07, .0, d);

    vec2 o2 = (fract(uv*a.x*vec2(1., 2.)) -.5)/vec2(1., 2.);
    d = length(o2);

    float m2 = smoothstep(.3*(.5 - st.y), .0, d)*smoothstep(-.1, .1, st.y-p1.y);
    //if(st.x > .46 || st.y > .49) m1 = 1.;

	return vec2(m1*o1*33.+m2*o2*10.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime*.01;
	vec2 rainDistort = Rain(uv*.99, t)*1.;
    rainDistort -= Rain(uv*.2, t)*.8;

    uv.x += sin(uv.y*50.)*.02;
	uv.y += sin(uv.x*170.)*.005;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+vec3(uv-rainDistort, uv.x)+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
