vec3 kv(vec2 a, vec2 b, vec2 c, vec3 attels)
{
  	vec2 point = step(b, a) - step(b + c, a);
    return attels * (point.x * point.y);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 xy = fragCoord.xy / iResolution.xy;
    vec3 tc = texture(iChannel0,xy).rgb;
    vec3 kv1 = kv(xy, vec2(0.03, 0.05), vec2(0.45, 0.42), tc);
    kv1.r = 0.0;
    vec3 tc1 = texture(iChannel0,xy).rgb;
    vec3 kv2 = kv(xy, vec2(0.55, 0.53), vec2(0.42, 0.42), tc1);
    kv2.g = 0.0;
    vec3 tc2 = texture(iChannel0,xy).rgb;
    vec3 kv3 = kv(xy, vec2(0.03, 0.53), vec2(0.45, 0.42), tc2);
    vec3 tc3 = texture(iChannel0,xy).rgb;
    vec3 kv4 = kv(xy, vec2(0.55, 0.05), vec2(0.42, 0.42), tc3);
    kv4.b = 0.0;


    vec3 attels = kv1 + kv2 + kv3 + kv4;

 	fragColor = vec4(attels, 1.0);

}
