void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // sound is 512x2 texture

    float fft = texture( iChannel0,vec2(uv.x,0.25)).x; // first row is fft
    float wave = texture( iChannel0,vec2(uv.x,0.75)).x; // second row is wave

    float col=0.0;


    //show fft in first half
    if(uv.y>0.5){
        if(fft>(uv.y*2.0-1.0)){
            col = 1.0;
        }
        else{
            col = 0.0;
        }
    }

    //show wave in second half
    else{
        if(wave>(uv.y*2.0)){
            col = 1.0;
        }
        else{
            col = 0.0;
        }
    }

	fragColor = vec4(vec3(col),1.0 );
}
