vec4 deres(vec4 blub, float detail) {
  ivec4 iblub = ivec4(blub*detail);
  return vec4(iblub)/detail;
}
vec3 deres(vec3 blub, float detail) {
  ivec3 iblub = ivec3(blub*detail);
  return vec3(iblub)/detail;
}
vec2 deres(vec2 blub, float detail) {
  ivec2 iblub = ivec2(blub*detail);
  return vec2(iblub)/detail;
}

float bayer(in float order, in vec2 coord) {
	const float MIPBIAS = -10.0;
    vec2 c = vec2(mod(coord.xy, order));
    return texture(iChannel0, c.xy / iChannelResolution[0].xy, MIPBIAS).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 pixel = texture(iChannel1, uv); 
    vec4 pixel2 = texture(iChannel2, uv);
    if(pixel2.g >= .5 && pixel2.r <= .5 && pixel2.b <= .5)
    	pixel2.a = 0.;
    else {
        pixel2.rgb =  pixel2.rgb + 0.5 - bayer(4., fragCoord.xy);
        pixel2.rgb = deres(pixel2.rgb, 3.);
    }
    if(pixel2.a != .0)
        pixel.rgb = pixel2.rgb;
	fragColor = pixel;
}
