//#define VISUALIZE_UV
#define VISUALIZE_CIRCLE_DF
//#define VISUALIZE_COOL

#define SAMPLES 1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= 3.5+cos(iTime*.5)*2.5;//4.5+sin(iTime*.25)*2.;
    vec2 ruv = uv;

    fragColor = vec4(0.);

    for (int b = 0; b < SAMPLES; b++) {

      //  uv = ruv+(cos(uv*.7521+float(b)*.42)+cos(uv.yx*.2234+float(b)*2.42))*.;

        for (int i = 0; i < 3; i++) {
            float ang = 0.;//length(uv)*.1+
                //length(reflect(cos(uv),sin(uv.yx)))*.1;
            uv = reflect(sin(uv),cos(uv));//*mat2(sin(ang),cos(ang),-cos(ang),sin(ang));
        }

        #ifdef VISUALIZE_UV
        fragColor += vec4(cos(normalize(abs(uv))*12.)*.5+.5,0.,1.0);
        #endif

        #ifdef VISUALIZE_CIRCLE_DF
        float dist = max(0.,1.-length(mod(abs(uv),1.)-.5)) + iTime*.03;
        fragColor += vec4(sin(dist*3.5+.8),
                         cos(dist*4.6+.2),
                         sin(dist*2.6), 1.)*.5+.5;
        #endif

        #ifdef VISUALIZE_SQUARE_DF

        #endif

        #ifdef VISUALIZE_COOL
        fragColor += vec4(cos(uv*10.)*.5+.5
                        ,cos(length(uv)*4.)*.5+.5,1.);
        #endif

    }

    fragColor /= float(SAMPLES);
}
