void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy;
	fragCoord = (fragCoord -.5*R)/R.y;

    float l = length(fragCoord);
    fragColor = smoothstep(2.4,5.4,1.6*sin(l*30. - 6.*iTime) + .4/l - vec4(0));
}

