void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float proc = 6.0;
    vec2 xy = fragCoord.xy / iResolution.xy; // Condensing this into one line
    vec4 texColor = texture(iChannel0,xy); // Get the pixel at xy from iChannel0
    //vec4 TempColor = texture(iChannel2,xy);
    float tem;
    if (fragCoord.y < iTime/proc*iResolution.y) 
        {
           texColor = vec4(0,0,0,0.0); 
        }
    if (fragCoord.y > (iTime/proc*iResolution.y)-50.0) 
        {
            if (fragCoord.y < iTime/proc*iResolution.y) 
            {
                tem = xy.y+50.0;
                texColor = texture(iChannel1,vec2(xy.x,tem));
            }           
        }
    fragColor = texColor; // Set the screen pixel to that color
}
