// pseudo synced visual primarily sound shader

void mainImage( out vec4 k, in vec2 p )
{
    vec2 rz = iResolution.xy,
         uv = p/rz.xy;

    k = vec4(.5 + .5*cos(iTime+uv.xyx+vec3(0,2,4)),1.0);

    uv -= .5;
    vec2 st = vec2(sin(uv.y), cos(uv.x)),
         vt = mod(.01*iTime,.01)*st; 

    k -= mainSound( in int samp,dot(vt*vt, rz)).xyyy;
}

#define FREQ 56.
#define TAU 6.2831

float sampler( float wt, float dt )
{
    float vt = mod(TAU*wt, 7.),
          ut = mod(vt+TAU*dt, 3.),
          t = ut*(vt+ut*(vt+dt)),
    	  f = FREQ;
//    	  f = mod(FREQ+2.*t, 100.);  // walk up

    return dt*sin(f*vt)* (.1*sin(1./vt) * fract(sin(ut)));
}

vec2 mainSound( in int samp, float wt )
{
    float m = 0.;
    for(float dt = 1.; dt>.0 ; dt-=.01)
        m += sampler(wt, dt);

	return vec2(sin(m));
}
