void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float samples = 30.0;
	vec2 direction = vec2(0.866/(iResolution.x/iResolution.y)*0.05, -0.025);
    float bokeh = abs(cos(iTime*.5));

    vec2 uv = fragCoord/iResolution.xy;

    vec4 sum = vec4(0.0);
    vec4 msum = vec4(0.0);
    float delta = 1.0/samples;
    float di = bokeh/(samples-1.0);
    for (float i = -0.5*bokeh; i < 0.501*bokeh; i += di) {
        vec4 color = texture(iChannel0, uv+direction*i);
        sum += color * delta;
        msum = max(color, msum);
    }

    fragColor = mix(sum, msum, bokeh);
}

