/*
===+
A lot of algorithms have been taken from a multitude of sources, which are listed in 'common'
I recommend comparing visual results to something like this:
	http://davidbau.com/conformal/#z
	(there's an infobox in the top-right corner)
Advice on to how optimize\reduce this code would be appreciated.
Also, you can use this code elsewhere if you like.
+===
*/

#define SIN(x) (sin(x)*.5+.5)
#define COS(x) (cos(x)*.5+.5)
#define SIN2(x) (SIN(x+halfpi))
#define COS2(x) (COS(x+halfpi))

#define VECTIME vec2(cos(iTime+halfpi), cos(iTime))
#define VECTIME2 vec2(cos(t+halfpi), cos(t))

      //|z| is near 0
const vec3 c1 = vec3(1.);
 	  //|z| => +infinity
const vec3 c3 = vec3(0.2);
	  //|z| is near 1
const vec3 c2 = vec3(1.)-c3;


//===++===//

//General use.

//Returns radian, of range -π, π
float arg(vec2 z) { return atan(z.y,z.x); }

//Returns radian, of range 0, τ (2π)
float arg2(vec2 z) { return arg(z)+pi; }

//Returns radian, of range -1, 1
float arg3(vec2 z) { return arg(z)/pi; }

//Returns degree, of range 0, 360
float argd(vec2 z) { return arg2(z)*rtod; }

//Returns normalized value, of range 0, 1
float narg(vec2 z) { return arg2(z)/twopi; }

float mag(vec2 z)  { return length(z); }
vec2 conj(vec2 z)  { return vec2(z.x, -z.y); }

//===++===//
//===++===//

//Basic arithmetic.

vec2 cmul(vec2 z, vec2 w) { return vec2(z.x*w.x-z.y*w.y, z.x*w.y+z.y*w.x); }
vec2 cmul(vec2 z, float w) { return z*w; }
vec2 csquare(vec2 z) { return vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y); }
vec2 cdiv(vec2 z, vec2 w) { return vec2((z.x*w.x+z.y*w.y)/(w.x*w.x+w.y*w.y),(z.y*w.x-z.x*w.y)/(w.x*w.x+w.y*w.y)); }
vec2 cdiv(vec2 z, float w) { return cdiv(z, vec2(w, 0.)); }
vec2 cdiv(float z, vec2 w) { return cdiv(vec2(z, 0.), w); }
//Reciprocal
vec2 recip(vec2 z) { return vec2(z.x/(z.x*z.x+z.y*z.y), -z.y/(z.x*z.x+z.y*z.y)); }

vec2 fromPol(float mag, float arg) { return vec2(mag*cos(arg), mag*sin(arg)); }
vec2 fromPol(vec2 z) { return vec2(mag(z)*cos(arg(z)), mag(z)*sin(arg(z))); }

vec2 csqrt(vec2 z) { return fromPol(sqrt(mag(z)), arg(z)/2.); }

vec2 imMul(vec2 z) { return vec2(-z.y, z.x); }

//===++===//
//===++===//

//Exponentiables

vec2 cln (vec2 z) { return vec2(log(mag(z)), arg(z)); }
vec2 cln (float z){ return cln(vec2(z, 0.)); }
vec2 clog(vec2 b, vec2 a) { return cdiv(cln(b), cln(a)); }
vec2 clog(vec2 b, float a) { return cdiv(cln(b), cln(a)); }
vec2 clog(float b, vec2 a) { return cdiv(cln(b), cln(a)); }
vec2 cexp(float x) { return vec2(cos(x), sin(x)); }
vec2 cexp(float x, float y) { return exp(x)*cexp(y); }
vec2 cexp (vec2 z) { return cexp(z.x, z.y); }
vec2 cexp2(vec2 z){ return cexp(z.y, z.x); }

vec2 cpow(  vec2 z,  vec2 w)  { if(w==vZero) return vOne; if(z==vZero) return vZero; return cexp(cmul(w, cln(z))); }
vec2 cpow(  vec2 z, float f) { return cpow(z, vec2(f, 0.)); }
vec2 cpow( float f,  vec2 w) { return cpow(vec2(f, 0.), w); }

vec2 croot( vec2 w,  vec2 z) { return cpow(z, recip(w)); }
vec2 croot( vec2 w, float f) { return cpow(f, recip(w)); } 
vec2 croot(float f,  vec2 z) { return cpow(z, 1./f); }

//===++===//
//===++===//

vec2   csin(vec2 z) { return vec2(sin(z.x)*cosh(z.y), cos(z.x)*sinh(z.y)); }
vec2   ccos(vec2 z) { return vec2(cos(z.x)*cosh(z.y), -(sin(z.x)*sinh(z.y))); }
vec2   ctan(vec2 z) { return cdiv(csin(z), ccos(z)); }
vec2   ccot(vec2 z) { return cdiv(ccos(z), csin(z)); }
vec2   csec(vec2 z) { return recip(ccos(z)); }
vec2   ccsc(vec2 z) { return recip(csin(z)); }

vec2  casin(vec2 z) { return -imMul(cln(imMul(z)+csqrt(vOne-csquare(z)))); }
vec2  cacos(vec2 z) { return -imMul(cln(z+csqrt(csquare(z)-vOne))); }
vec2  catan(vec2 z) { return (imMul(cln(vOne-imMul(z))-cln(vOne+imMul(z))))/2.; }
vec2  cacot(vec2 z) { return catan(recip(z)); }
vec2  casec(vec2 z) { return cacos(recip(z)); }
vec2  cacsc(vec2 z) { return casin(recip(z)); }

vec2  csinh(vec2 z) { return vec2(sinh(z.x)*cos(z.y), cosh(z.x)*sin(z.y)); }
vec2  ccosh(vec2 z) { return vec2(cosh(z.x)*cos(z.y), sinh(z.x)*sin(z.y)); }
vec2  ctanh(vec2 z) { return cdiv(csinh(z), ccosh(z)); }
vec2  ccoth(vec2 z) { return cdiv(ccosh(z), csinh(z)); }
vec2  csech(vec2 z) { return recip(ccosh(z)); }
vec2  ccsch(vec2 z) { return recip(csinh(z)); }

vec2 casinh(vec2 z) { return cln(z+csqrt(csquare(z)+vOne)); }
vec2 cacosh(vec2 z) { return cln(z+cmul(csqrt(z+vOne), csqrt(z-vOne))); }
vec2 catanh(vec2 z) { return (cln(cdiv(vOne+z, vOne-z)))/2.; }
vec2 cacoth(vec2 z) { return (cln(cdiv(z+vOne, z-vOne)))/2.; }
vec2 casech(vec2 z) { return cln(recip(z)+cmul(csqrt(recip(z)+vOne), csqrt(recip(z)-vOne))); }
vec2 cacsch(vec2 z) { return cln(recip(z)+csqrt(recip(csquare(z))+vOne)); }

//Note, some of the inverse functions behave a little strangely, namely cosine.
//I think that's due to the nature of complex numbers.

//===++===//
//===++===//


//Helps decide some functional coloring, for points near 1, 0, or some value representing infinity. (16 in this case)
vec3 checkDist(float val, vec3 c1, vec3 c2, vec3 c3, vec3 col)
{
    if (isNear(val, 0., prec))
        return c1;
    if (isNear(val, 1., prec))
        return c2;
    if (val >= 16.)
        return c3;
    return col;
}

#define ZOOM 10.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float norm = iResolution.y/iResolution.x;

    //Normalized and not mouse positions.
    vec2 l = ZOOM*((iMouse.xy/iResolution.xy)-.5);
    l.y*=iResolution.y/iResolution.x;

    vec2 pnt = l;
    //lock(pnt, iChannel0);
    if(keyClick(65, iChannel0)) pnt = roundTo(pnt, .25);
    if(keyClick(9, iChannel0)) pnt = normalize(pnt);
    vec2 tpnt = VECTIME2;

    //The 'normalized' pixel coordinate. AKA, each point in the complex plane.
    vec2 z = fragCoord/iResolution.xy;
    z -= .5;
    z.y *= norm;
    z*=ZOOM;

    //++=The actual function applied to each point z=++//
    vec2 pixelComp = z;

    float magnitude = mag(pixelComp);
    float argument = narg(pixelComp);

    float hue  = argument;
    float hue2 = 0.;

    //Lighter = |z| => 0.
    // Darker = |z| => infinity.
    float val = pow(1.4, -magnitude);
    hue -= hue2;
    float sat = 1.;
    vec3 col = hsvToRGB(hue, sat, val);
    //Displays all zeros, ones, and infinities.
    col = checkDist(magnitude, c1, c2, c3, col);

    #ifndef COMPAT
    if(keyClick(81, iChannel0)) 
        grid(fragCoord, .9, vec3(0., 1., 0.), vec3(0.3, 0.5, 0.8), iResolution.xy/2., iResolution.x/ZOOM, col);
    //Draws a point at the coordinate of VECTIME2 in the complex plane
    point(pnt, z, ZOOM/1., vec3(0.6, 0.3, 0.33), col);
    point(tpnt, z, ZOOM/1., vec3(1.), col);
    #endif
    fragColor = vec4(col, 1.);
}

/* Sources:
===+
Exponentiation:       https://goo.gl/T7NbTu
Complex Numbers:      https://goo.gl/jzXvvb
Trig. Functions:      https://goo.gl/aQuPsx   Inverses: https://goo.gl/X1wLHA
Hyperbolic Functions: https://goo.gl/34NSjJ   Inverses: https://goo.gl/zxP8PJ
C# Complex class:     https://goo.gl/X2CWm1
Milefoot.com:         https://goo.gl/C3va8S
+===
*/

//#define COMPAT

//Precision variable.
const float prec = 0.05;

const float e = exp(1.);
const float pi = acos(-1.);
const float twopi = acos(-1.)*2.;
const float halfpi = acos(-1.)/2.;

//Radians to degrees, acronymed rtod.
const float rtod = 180./pi;

const vec2 vOne 	= vec2(1.0, 0.0);
const vec2 vImagOne = vec2(0.0, 1.0);
const vec2 vZero 	= vec2(0.0, 0.0);
const vec2 vComp 	= vec2(1.0, 1.0);

//===++===//
//===++===//

vec3 hsvToRGB(float h, float s, float v)
{
    vec3 z0 = fract(h+vec3(3.,2.,1.)/3.);  //if h==0 => vec3(0, 2/3, 1/3)
    vec3 z1 = abs(z0*6.-3.);               //if h==0 => vec3(3, 1, 1)
    vec3 e0 = vec3(1.);			   	       
    vec3 e1 = clamp((z1-1.),0.,1.);        //if h==0 => vec3(1, 0, 0)
    return mix(e0,e1,s)*v; //if v==1 && s==1 && h==0 => vec3(1, 0, 0)
}

vec3 hslToRGB(float h, float s, float l)
{
    float v = (2.*l+s*(1.-abs(2.*l-1.)))/2.;
    float sv = (2.*(v-l))/v;
    return hsvToRGB(h, sv, v);
}

//===++===//
//===++===//

float map(float n, float start1, float stop1, float start2, float stop2) { return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2; }
bool isBetween(float a, float mn, float mx) { return (a >= mn && a <=mx); }
bool isNear(float val, float compare, float p) { return isBetween(val, compare-p, compare+p); }
bool isNear(vec2 val, float compare, float p)  { return isNear(val.x, compare, p)   && isNear(val.y, compare, p); }
bool isNear(vec2 val, vec2 compare, float p)   { return isNear(distance(compare, val), p, p); }

//===++===//
//===++===//

//Draws a point at a given position, relative to 'pix', with radius 'r' and color 'cl'
//Honestly, I'm not sure how 'pix' really affects the output, but it seems to work well.
void point(vec2 pos, vec2 pix, float r, vec3 cl, out vec3 col)
{
    if(isNear(pix, pos, r/1024.))
        col = cl;
}

//Draws a grid. A bit complicated, but it works.d
void grid(vec2 pix, float thickness, vec3 col, vec3 originCol, vec2 space, float spacing, out vec3 color)
{
    if(isNear(mod(space.x-pix.x, spacing), 0., thickness) || isNear(mod(space.y-pix.y, spacing), 0., thickness))
        color = col;
    if(isNear(pix.x, space.x, thickness) || isNear(pix.y, space.y, thickness))
        color = originCol;
}

//TODO: make this look like David Bau's checkers.
void checker(vec2 pix, float thickness, vec3 col, float spacing, out vec3 color)
{
    if(isNear(mod(pix.x, spacing), 0., thickness)||isNear(mod(pix.y, spacing), 0., thickness)) color = col;
}

//===++===//
//===++===//

float roundTo(float a, float p) { return round(a/p)*p; }
vec2 roundTo(vec2 a, float p) { return vec2(round(a.x/p)*p, round(a.y/p)*p); }

bool keyClick(int ascii, sampler2D channel) {
	return (texture(channel, vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}
void lock(out vec2 a, sampler2D channel) 
{    
    if(keyClick(17, channel)) a = vec2(a.x, 0.);
    else if (keyClick(16, channel)) a = vec2(0., a.y);
}
