vec2 xy;

float Sin() {
    float a = xy.x / 10.0;
	return length(vec2(0.0, xy.y - 10.0 * sin(a))) - 10.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    xy = fragCoord - iResolution.xy / 2.0;
    fragColor = vec4(step(Sin(), 0.0));
    //float d = Sin();
    //fragColor = vec4(-d, d, 0.0, 1.0) / 10.0;
}
