void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.4 * min(iResolution.x, iResolution.y);
    vec3 blur = 30.0 + 28.0 * sin(iTime * vec3(1, 1.1, 1.2));
    vec2 c = 0.5 * iResolution.xy;
    vec3 dist = smoothstep(-blur, blur, vec3(abs(distance(fragCoord, c) - radius)));
	fragColor = vec4(
        dist,
        1.0
    );
}
