void mainImage( out vec4 o, vec2 c )
{
    o-=o;
    float l,
          T = iTime/71.-.194;
    vec3 R = iResolution;
    #define O(S,f,m) l = length(c); o = l>f || o.b>0.? o : vec4(1,1,1,2)-texture(S,c/sqrt(1.1-l/f)*m + vec2(-T/f,0))
    c = (c-R.xy*.5)*5.4/R.y;
    O(iChannel1,1.,.3);
    c = c - vec2(sin(T)/.3, cos(T=-T)-.7);
    O(iChannel0,.3,1.).r;
}


