float norm;
vec2 fragCoord;
vec4 color;

float Norm(float num)
{
	float res = pow(num, norm);
	return res;
}

void Shape()
{
	float radius = 0.4;

	vec2 pixRatio = fragCoord.xy / iResolution.xy;
	vec2 centre = vec2(0.5, 0.5);
	vec2 distVector = pixRatio - centre;
	distVector = vec2(abs(distVector.x), abs(distVector.y));

	float dist = Norm(distVector.x) + Norm(distVector.y);

	float rad = Norm(radius);

	vec2 uv = fragCoord.xy / iResolution.xy;
	color = vec4(uv,0.5+0.5,1.0);

	if(dist > rad)
		color = vec4(0.0, 0.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 iFragCoord )
{
    int fps = 60;
    int restartSeconds = 10;
    fragCoord = iFragCoord;
	norm = float(iFrame % (fps * restartSeconds)) / float(fps);
	Shape();
    fragColor = color;
}
