#define DRAW_TUNNEL 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = 2.*uv-1.;
    p.x *= iResolution.x/iResolution.y;
   float bail=2000.;
   vec3 col = vec3(0.);
    vec3 final_col = vec3(0.);

    float x = atan(p.y,p.x);
    float y = .5/dot(p,p)+iTime;

    vec3 tunnel_col = vec3(mod(step(mod(x,.2),.1)+step(mod(y,.2),.1),2.));
    tunnel_col *= .2;
    tunnel_col *= 1.-smoothstep(1.2,0.,length(p));
	final_col += DRAW_TUNNEL*tunnel_col;

    vec2 oth= vec2(.05+mod(.1*iTime,.20)); 
    for (int i=0; i < 30; i++)
    {
        p += vec2(p.x*p.x-p.y*p.y,p.x*p.y*2.)+oth;
        col = 2.*vec3(cos(p.x),p.y,p);
        if (length(p) > bail) 
        {
            col *= vec3(0.);
            break;
        }
    }

    final_col += col;

	fragColor = vec4(final_col,1.0);
}
