const float QRAT = 1./16.;
vec3 getText(float i, float j) {
    float x = mod(i,16.);
    float y = j-floor(i/16.);
    return texture(iChannel0,vec2(x*QRAT,y*QRAT)).rgb;
}
vec3 alphabet(int i, vec2 uv) {
	return getText(uv.x+1.+float(i),uv.y+11.);
}
const int A=0,B=1,C=2,D=3,E=4,F=5,G=6,H=7,I=8,J=9,K=10,L=11,M=12,N=13,O=14,P=15,Q=16,R=17,S=18,T=19,U=20,V=21,W=22,X=23,Y=24,Z=25;
#define CHAR(q, i) if (x<q) l=i;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 ouv = fragCoord.xy / iResolution.xy,uv=ouv;
    float its = texture(iChannel1,vec2(0,0.7)).r;
    uv-=(uv-.5)*its/13.;
    vec3 o = vec3(0);// vec3(1.-distance(uv,vec2(.5)));
    float t = 3.*60.+53.;
    float ti = iTime/t;
    if(abs(uv.y*11.-2.1)<.5) {
    	if(uv.x<ti)o=vec3(sin(uv.x-iTime)*.5+.6);    
    }
    int l = 0;
    float x = floor(uv.x*11.);
    CHAR(1.,X) else CHAR(2.,E) else CHAR(3.,N) else CHAR(4.,O) else CHAR(5.,G) else CHAR(6.,E) else CHAR(7.,N) else CHAR(8.,I) else CHAR(9., S) else CHAR(10.,I) else CHAR(11.,S)
    vec3 c=alphabet(l,vec2(fract(uv.x*11.),uv.y*11.-2.));
    if(uv.y*11.-2.>1.||uv.y*11.-2.<0.){
        c=vec3(0);} else {o=texture(iChannel1,vec2(uv.x,.1)).rrr/2.;}
    c=c.rrr*c.rrr;
    //o*=sin(clamp(iTime,0.,5.)/10.*acos(-1.));
    o+=c;
    if(uv.y*11.-2.>1.) {
    	float q = uv.y*11.-3.;
        float val = texture(iChannel1,vec2(floor(uv.x*50.)/50.,0.)).r;
        val*=val;
        float cen = floor(uv.x*50.)/50.+.5/50.;
        if(q/7.+.05<val) o=vec3(val*sin(q*acos(-1.)/5.)+.2)*(1.5-abs(uv.x-cen)/(1./50.))-uv.y/2.;
    }
    if(iTime<1.) {
    	o*=iTime;    
    }
    if(iTime>t-1.)
    {
    	o-=(iTime-(t-1.));
    }
    o*=1.2-length(ouv-.5)*1.7;
	fragColor = vec4(o,1.0);
}
