float meanLuminance(vec4 frag) {
    return (frag.x + frag.y + frag.z) / 3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 effect = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec4 cam = texture(iChannel0, uv);
    vec4 self = texture(iChannel1, uv);

    vec4 highLight = cam;
    if(meanLuminance(cam) < 0.9) {
    	highLight = vec4(0.0);
    }

    self = self + (highLight * vec4(0.02));
    fragColor = self;
}
