void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}




// ------------------------------------------------------------------------------

// extra globals or uniforms
float myglobal;

// def here the extra global initializations + prior change to fragCoord
void _pre(inout vec2 U ) { 
    myglobal = iTime; 
    U = iResolution.xy-U; 
}

// def here post-treatments to fragColor
void _post(inout vec4 o) { 
    o = 1.-o; 
}

// the trick that extends the usual mainImage
#define mainImage(o,u)   vec2 _u=u; _pre(_u);  mainImage(o,_u); _post(o);
