void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float maxIter = 256.0;//(sin(iTime / 1.0) + 1.0) / 2.0 * 40.0;
    vec2 r = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;// / (((iTime / 100.0) * 1000.0) + 1.0);
    vec2 z = vec2(0.0);//vec2( (sin(iTime)) / 2.0,  (cos(iTime / 2.0)));
    float zoom = pow(0.00001, sin(iTime * 0.25)) + 0.1;//(sin(iTime) + 2.0);
    vec2 xy = vec2(r.x, r.y); //r.x + cos(iTime / 4.0) + 2.0
    vec2 c = vec2(-0.745592, 0.10003) + xy / (zoom * 10.0);
    float l = 0.0;
    for(int i = 0; i >= 0; i++)
	{
    	z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
		if(z.x * z.y > maxIter || l > maxIter) break; // z.x * z.x + z.y * z.y > maxIter to show regular
		l += 1.0;
	}

    int v = int(mod(l, 16.0)); //l + 1.0 - log(log(sqrt(z.x * z.x + z.y * z.y))) / log(2.0)
    vec3 co = vec3(0.0);
    if(v == 1) // wtf had to type out this monster because GLSL doesn't like dynamic array indexing
        co = vec3(25, 7, 26);
    else if(v == 2)
        co = vec3(9, 1, 47);
    else if(v == 3)
        co = vec3(4, 4, 73);
    else if(v == 4)
        co = vec3(0, 7, 100);
    else if(v == 5)
        co = vec3(12, 44, 138);
    else if(v == 6)
        co = vec3(24, 82, 177);
    else if(v == 7)
        co = vec3(57, 125, 209);
    else if(v == 8)
        co = vec3(134, 181, 229);
    else if(v == 9)
        co = vec3(211, 236, 248);
    else if(v == 10)
        co = vec3(241, 233, 191);
    else if(v == 11)
        co = vec3(248, 201, 95);
    else if(v == 12)
    	co = vec3(255, 170, 0);
    else if(v == 13)
        co = vec3(204, 128, 0);
   	else if(v == 14)
        co = vec3(153, 87, 0);
    else if(v == 15)
        co = vec3(106, 52, 3);

    co /= 256.0;    
    fragColor = vec4(co, 1.0);//0.0, 1.0 - l * 5.0 / maxIter, 1.0 - l * 10.0 / maxIter), 1.0);
}
