// http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = 0.5 + 0.5 * vec2(sin(iTime), cos(iTime * pow(2.0, 0.5)));
    float epsilon = 1.0 / iResolution.x;
    if (iMouse.z > 0.0) {
        mouse = iMouse.xy / iResolution.xy;
    }
    vec2 gridPos = uv - mod(uv, mouse + epsilon);
    fragColor = vec4(hsv2rgb(vec3(gridPos.x, 1.0 - gridPos.y, 1.0)), 1.0);
}
