vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(sin(d)*2.0, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    vec2 center = iResolution.xy * 0.5;
    float radius = sin(iTime/5.0) * .25 * iResolution.y;
    vec3 red = vec3(0.9, 0.2, 0.05);

    vec4 bg = vec4(0.9, 0.9, 0.95, 1.0);
    vec4 circle = circle(uv, center, radius, red);


	fragColor = mix(bg, circle, circle.a);
}
