void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5);

    float d = length(q);

    // transition from purple to orange with a specific power curve
    vec3 col = mix(vec3(0.6, 0.2, 0.7), vec3(1.0, 0.5, 0.0), pow(d*1.5,2.0));

    // make the stripes in the starburst and rotate them
    float r0 = cos(atan(q.y, q.x) * 40.0 + iTime * 10.0) + 0.5;

    // add some waviness to the stripes
    r0 += 0.2 * cos(100. * q.y + iTime * 12.0);
    float r1 = 0.03;

    float blur = 0.5;
    col *= smoothstep(r0, r0+blur, d);
    col *= smoothstep(r1, r1+blur, d+0.08);

	fragColor = vec4(vec3(col),1.0);
}
