void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cv = vec2((sin(iTime*3.1)/2.0+0.5)*iResolution.x,(sin(iTime*2.6)/2.0+0.5)*iResolution.y);
    float size = (sin(iTime*6.0)*0.5+0.5)*75.0+25.0+sin(iTime*23.5)*25.0+sin(iTime*87.5)*5.0;
    float pi = 3.1415926536;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord-cv;

	vec2 uv2 = fragCoord/iResolution.xy;
    if (length(uv)>size)
        fragColor = texture(iChannel0, uv2);
    else
    {
    	fragColor = vec4(max(0.0,sin(iTime*2.0)), max(0.0,sin(iTime*2.0+2.0*pi/3.0)), max(0.0,sin(iTime*2.0+4.0*pi/3.0)), 0.0);
    }
}
