#define MINIMUM 0.85

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel0, uv);

    // Output to screen
    if (dot(color.xyz, vec3(0.2125, 0.72154, 0.0725)) > MINIMUM) 
        fragColor = color;
    else fragColor = vec4(0);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Gaussian values
    float offset[5]= float[](0.0, 1.0, 2.0, 3.0, 4.0);
    float gaussVal[5] = float[](0.2270270270, 0.1945945946, 0.1216216216, 0.0540540541, 0.0162162162);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
    fragColor *= gaussVal[0];

    // Output to screen
    for(int i = 1; i < 5; i++) {
        fragColor += texture(iChannel0,(fragCoord+vec2(offset[i],0.0))/iResolution.xy)*gaussVal[i];
        fragColor += texture(iChannel0,(fragCoord-vec2(offset[i],0.0))/iResolution.xy)*gaussVal[i];
    }

}

